  "ASL:HELLO": "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!-- ASL HELLO: Wave from forehead outward -->\n<sigml>\n  <hns_sign gloss=\"HELLO_ASL\">\n    <hamnosys_manual>\n      <hamflathand/><hamextfingerul/><hampalml/><hamforehead/><hamtouch/>\n      <hammoveo/><hamsmallmod/>\n    </hamnosys_manual>\n    <hamnosys_nonmanual>\n      <hnm_mouthing word=\"hello\"/>\n    </hamnosys_nonmanual>\n  </hns_sign>\n</sigml>",
  "ASL:SERIOUS": "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!-- ASL SERIOUS: Index finger touching chin and moving down -->\n<sigml>\n  <hns_sign gloss=\"SERIOUS_ASL\">\n    <hamnosys_manual>\n      <hamfinger2/><hamextfingerul/><hampalml/>\n      <hamchin/><hamtouch/>\n      <hammoved/><hamsmallmod/>\n    </hamnosys_manual>\n    <hamnosys_nonmanual>\n      <hnm_mouthing word=\"serious\"/>\n    </hamnosys_nonmanual>\n  </hns_sign>\n</sigml>",
  "ASL:STORM": "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!-- ASL STORM: Both hands moving in circular turbulent motion -->\n<sigml>\n  <hns_sign gloss=\"STORM_ASL\">\n    <hamnosys_manual>\n      <hamfinger2345/><hamextfingeruo/><hampalmd/><hamseql/>\n      <hamfinger2345/><hamextfingeruo/><hampalmd/>\n      <hamneutralspace/>\n      <hamcircled/><hamcirclel/><hamlargemod/><hamrepeatfromstart/>\n    </hamnosys_manual>\n    <hamnosys_nonmanual>\n      <hnm_mouthing word=\"storm\"/>\n    </hamnosys_nonmanual>\n  </hns_sign>\n</sigml>",
  "ASL:WARNING": "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!-- ASL WARNING: Flat hand tapping back of other hand -->\n<sigml>\n  <hns_sign gloss=\"WARNING_ASL\">\n    <hamnosys_manual>\n      <hamflathand/><hamextfingeruo/><hampalmd/>\n      <hamneutralspace/>\n      <hammoveu/><hammoved/><hamrepeatfromstart/>\n    </hamnosys_manual>\n    <hamnosys_nonmanual>\n      <hnm_mouthing word=\"warning\"/>\n    </hamnosys_nonmanual>\n  </hns_sign>\n</sigml>",
  "ISL:HELLO": "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!-- ISL HELLO: Namaste gesture - palms together at chest -->\n<sigml>\n  <hns_sign gloss=\"HELLO_ISL\">\n    <hamnosys_manual>\n      <hamflathand/><hamextfingeruo/><hampalml/><hamseql/>\n      <hamflathand/><hamextfingeruo/><hampalml/>\n      <hamchest/><hamtouch/>\n      <hammoveu/><hamsmallmod/>\n    </hamnosys_manual>\n    <hamnosys_nonmanual>\n      <hnm_mouthing word=\"namaste\"/>\n    </hamnosys_nonmanual>\n  </hns_sign>\n</sigml>",
  "ISL:SERIOUS": "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!-- ISL SERIOUS: Flat hand at forehead with firm downward movement -->\n<sigml>\n  <hns_sign gloss=\"SERIOUS_ISL\">\n    <hamnosys_manual>\n      <hamflathand/><hamextfingeruo/><hampalmd/>\n      <hamforehead/><hamtouch/>\n      <hammoved/><hamsmallmod/>\n    </hamnosys_manual>\n    <hamnosys_nonmanual>\n      <hnm_mouthing word=\"gambhir\"/>\n    </hamnosys_nonmanual>\n  </hns_sign>\n</sigml>",
  "ISL:STORM": "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!-- ISL STORM: Hands moving upward with swirling motion (toofan) -->\n<sigml>\n  <hns_sign gloss=\"STORM_ISL\">\n    <hamnosys_manual>\n      <hamfinger2345/><hamextfingeruo/><hampalmo/><hamseql/>\n      <hamfinger2345/><hamextfingeruo/><hampalmo/>\n      <hamneutralspace/>\n      <hammoveu/><hamcircled/><hamlargemod/><hamrepeatfromstart/>\n    </hamnosys_manual>\n    <hamnosys_nonmanual>\n      <hnm_mouthing word=\"toofan\"/>\n    </hamnosys_nonmanual>\n  </hns_sign>\n</sigml>",
  "ISL:WARNING": "<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<!-- ISL WARNING: Index finger waving side to side -->\n<sigml>\n  <hns_sign gloss=\"WARNING_ISL\">\n    <hamnosys_manual>\n      <hamfinger2/><hamextfingeruo/><hampalml/>\n      <hamneutralspace/>\n      <hammoveol/><hammoveor/><hamrepeatfromstart/>\n    </hamnosys_manual>\n    <hamnosys_nonmanual>\n      <hnm_mouthing word=\"savdhan\"/>\n    </hamnosys_nonmanual>\n  </hns_sign>\n</sigml>"
